import api from '../services/api';

const StudentList = ({ students, onEdit, onDelete }) => {
  return (
    <table className="table-auto w-full border">
      <thead>
        <tr>
          <th className="border px-4 py-2">Name</th>
          <th className="border px-4 py-2">Email</th>
          <th className="border px-4 py-2">Course</th>
          <th className="border px-4 py-2">Actions</th>
        </tr>
      </thead>
      <tbody>
        {students.map((student) => (
          <tr key={student.id}>
            <td className="border px-4 py-2">{student.name}</td>
            <td className="border px-4 py-2">{student.email}</td>
            <td className="border px-4 py-2">{student.course}</td>
            <td className="border px-4 py-2">
              <button onClick={() => onEdit(student)} className="bg-yellow-500 text-white p-1 mr-2">Edit</button>
              <button onClick={() => onDelete(student.id)} className="bg-red-500 text-white p-1">Delete</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default StudentList;
