// App.js
import React, { useState, useEffect } from "react";
import "./index.css";
import Sidebar from "./components/Sidebar";
import Overview from "./components/Overview";
import Inventory from "./components/Inventory";
import Analytics from "./components/Analytics";

export default function App() {
  const [page, setPage] = useState("overview");
  const [books, setBooks] = useState([]);
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [editingBook, setEditingBook] = useState(null);

  // Load data from localStorage on component mount
  useEffect(() => {
    const savedBooks = localStorage.getItem("bookstore-inventory");
    if (savedBooks) {
      setBooks(JSON.parse(savedBooks));
    }
  }, []);

  // Save to localStorage whenever books change
  useEffect(() => {
    localStorage.setItem("bookstore-inventory", JSON.stringify(books));
  }, [books]);

  // Add a new book
  const addBook = (newBook) => {
    const id = books.length > 0 ? Math.max(...books.map(book => book.id)) + 1 : 1;
    setBooks([...books, { ...newBook, id }]);
    setIsFormOpen(false);
  };

  // Update an existing book
  const updateBook = (updatedBook) => {
    setBooks(books.map(book => book.id === editingBook.id ? { ...updatedBook, id: editingBook.id } : book));
    setEditingBook(null);
    setIsFormOpen(false);
  };

  // Delete a book
  const deleteBook = (id) => {
    setBooks(books.filter(book => book.id !== id));
  };

  // Handle edit action
  const handleEdit = (book) => {
    setEditingBook(book);
    setIsFormOpen(true);
  };

  return (
    <div className="app">
      <Sidebar currentPage={page} onChangePage={setPage} />

      <main className="main-content">
        {page === "overview" && <Overview books={books} />}
        {page === "inventory" && (
          <Inventory 
            books={books} 
            onAdd={() => setIsFormOpen(true)}
            onEdit={handleEdit}
            onDelete={deleteBook}
            isFormOpen={isFormOpen}
            editingBook={editingBook}
            onAddBook={addBook}
            onUpdateBook={updateBook}
            onCloseForm={() => {
              setIsFormOpen(false);
              setEditingBook(null);
            }}
          />
        )}
        {page === "analytics" && <Analytics books={books} />}
      </main>
    </div>
  );
}
