import React, { useState } from "react";
import { addOrder } from "../services/api";

function OrderForm({ onAddOrder }) {
  const [formData, setFormData] = useState({
    customerName: "",
    product: "",
    quantity: "",
    orderDate: "",
    status: "Pending",
  });
  const [errors, setErrors] = useState({});
  const [successMsg, setSuccessMsg] = useState("");

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const validate = () => {
    const errs = {};
    if (!formData.customerName) errs.customerName = "Customer Name is required";
    if (!formData.product) errs.product = "Product is required";
    if (!formData.quantity || formData.quantity <= 0) errs.quantity = "Quantity must be positive";
    return errs;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSuccessMsg("");
    const validationErrors = validate();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    try {
      await addOrder(formData);
      setSuccessMsg("Order added successfully!");
      onAddOrder(); // Refresh order list
      setFormData({ customerName: "", product: "", quantity: "", orderDate: "", status: "Pending" });
      setErrors({});
    } catch (err) {
      console.error("Failed to add order:", err);
      setErrors({ apiError: "Failed to add order. Please try again." });
    }
  };

  return (
    <div className="order-form-wrapper">
      <form onSubmit={handleSubmit} className="order-form">
        <h2>Add New Order</h2>
        {errors.apiError && <p className="error">{errors.apiError}</p>}
        {successMsg && <p className="success">{successMsg}</p>}

        <input
          type="text"
          name="customerName"
          placeholder="Customer Name"
          value={formData.customerName}
          onChange={handleChange}
        />
        <input
          type="text"
          name="product"
          placeholder="Product"
          value={formData.product}
          onChange={handleChange}
        />
        <input
          type="number"
          name="quantity"
          placeholder="Quantity"
          value={formData.quantity}
          onChange={handleChange}
        />
        <input
          type="date"
          name="orderDate"
          value={formData.orderDate}
          onChange={handleChange}
        />
        <select name="status" value={formData.status} onChange={handleChange}>
          <option value="Pending">Pending</option>
          <option value="Completed">Completed</option>
        </select>
        <button type="submit">Add Order</button>
      </form>

      {/* Validation Errors */}
      {Object.keys(errors).length > 0 && !errors.apiError && (
        <div className="error-modal">
          <h3>Validation Errors</h3>
          <ul>
            {Object.values(errors).map((err, i) => (
              <li key={i}>{err}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default OrderForm;
