// components/Overview.js
import React from "react";
import StatCard from "./StatCard";

const Overview = ({ books }) => {
  const totalBooks = books.length;
  const booksInStock = books.filter(book => book.quantity > 0).length;
  const outOfStock = books.filter(book => book.quantity === 0).length;
  const lowStock = books.filter(book => book.quantity > 0 && book.quantity <= 5).length;

  return (
    <div className="page">
      <h1>Overview</h1>
      <div className="stats-grid">
        <StatCard title="Total Books" value={totalBooks} />
        <StatCard title="Books in Stock" value={booksInStock} />
        <StatCard title="Out of Stock" value={outOfStock} />
        <StatCard title="Low Stock" value={lowStock} />
      </div>
      
      <div className="recent-books">
        <h2>Recent Books</h2>
        {books.length > 0 ? (
          <ul>
            {books.slice(-5).map(book => (
              <li key={book.id}>
                <span className="book-title">{book.title}</span> by {book.author} 
                <span className={`quantity ${book.quantity === 0 ? 'out-of-stock' : ''}`}>
                  ({book.quantity} in stock)
                </span>
              </li>
            ))}
          </ul>
        ) : (
          <p>No books added yet.</p>
        )}
      </div>
    </div>
  );
};

export default Overview;
