import { useState, useEffect } from 'react';
import api from '../services/api';
import { addStudent } from '../services/api';

const StudentForm = ({ student, onSave, onCancel }) => {
  const [form, setForm] = useState({ name: '', email: '', course: '' });

  useEffect(() => {
    if (student) setForm(student);
  }, [student]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (student) {
        await api.put(`/students/${student.id}`, form);
      } else {
        await api.post('/students', form);
      }
      onSave();
    } catch (error) {
      console.error('Error saving student:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div className="relative">
        <span className="absolute left-3 top-3 text-blue-300">üë§</span>
        <input
          type="text"
          placeholder="Name"
          value={form.name}
          onChange={(e) => setForm({ ...form, name: e.target.value })}
          required
          className="w-full pl-10 pr-4 py-3 bg-blue-800/50 border border-blue-600 rounded-lg text-white placeholder-blue-300 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 hover:bg-blue-700/50"
        />
      </div>
      <div className="relative">
        <span className="absolute left-3 top-3 text-blue-300">üìß</span>
        <input
          type="email"
          placeholder="Email"
          value={form.email}
          onChange={(e) => setForm({ ...form, email: e.target.value })}
          required
          className="w-full pl-10 pr-4 py-3 bg-blue-800/50 border border-blue-600 rounded-lg text-white placeholder-blue-300 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 hover:bg-blue-700/50"
        />
      </div>
      <div className="relative">
        <span className="absolute left-3 top-3 text-blue-300">üìö</span>
        <input
          type="text"
          placeholder="Course"
          value={form.course}
          onChange={(e) => setForm({ ...form, course: e.target.value })}
          required
          className="w-full pl-10 pr-4 py-3 bg-blue-800/50 border border-blue-600 rounded-lg text-white placeholder-blue-300 focus:outline-none focus:ring-2 focus:ring-blue-400 transition-all duration-300 hover:bg-blue-700/50"
        />
      </div>
      <div className="flex space-x-4">
        <button
          type="submit"
          className="flex-1 bg-gradient-to-r from-green-500 to-blue-500 hover:from-green-600 hover:to-blue-600 text-white py-3 rounded-lg font-semibold transition-all duration-300 transform hover:scale-105 shadow-lg"
        >
          üíæ Save
        </button>
        <button
          type="button"
          onClick={onCancel}
          className="flex-1 bg-gradient-to-r from-gray-500 to-gray-600 hover:from-gray-600 hover:to-gray-700 text-white py-3 rounded-lg font-semibold transition-all duration-300 transform hover:scale-105 shadow-lg"
        >
          ‚ùå Cancel
        </button>
      </div>
    </form>
  );
};

export default StudentForm;
