// components/InventoryTable.js
import React from "react";

const InventoryTable = ({ books, onEdit, onDelete }) => {
  if (books.length === 0) {
    return <p className="no-books">No books in inventory. Add some books to get started.</p>;
  }

  return (
    <div className="table-container">
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Author</th>
            <th>Category</th>
            <th>Price</th>
            <th>Quantity</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {books.map((book) => (
            <tr key={book.id}>
              <td>{book.title}</td>
              <td>{book.author}</td>
              <td>{book.category}</td>
              <td>M{book.price}</td>
              <td>{book.quantity}</td>
              <td>
                <span className={`status ${book.quantity === 0 ? 'out-of-stock' : book.quantity <= 5 ? 'low-stock' : 'in-stock'}`}>
                  {book.quantity === 0 ? 'Out of Stock' : book.quantity <= 5 ? 'Low Stock' : 'In Stock'}
                </span>
              </td>
              <td>
                <div className="action-buttons">
                  <button onClick={() => onEdit(book)}>Edit</button>
                  <button onClick={() => onDelete(book.id)} className="delete-btn">Delete</button>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default InventoryTable;
