import React from 'react';

const Sidebar = ({ activeView, setActiveView }) => {
  const menuItems = [
    { id: 'dashboard', label: 'Dashboard', icon: 'fas fa-home' },
    { id: 'add', label: 'Add Product', icon: 'fas fa-plus-circle' },
    { id: 'manage', label: 'Manage Products', icon: 'fas fa-edit' },
    { id: 'analytics', label: 'Analytics', icon: 'fas fa-chart-bar' },
    { id: 'settings', label: 'Settings', icon: 'fas fa-cog' }
  ];

  return (
    <div className="sidebar">
      <div className="logo">
        <i className="logo-icon fas fa-book"></i>
        <span className="logo-text">BookHub</span>
      </div>
      
      <ul className="menu">
        {menuItems.map(item => (
          <li 
            key={item.id} 
            className={`menu-item ${activeView === item.id ? 'active' : ''}`}
          >
            <a 
              href="#!" 
              className="menu-link"
              onClick={(e) => {
                e.preventDefault();
                setActiveView(item.id);
              }}
            >
              <i className={item.icon}></i>
              <span>{item.label}</span>
            </a>
          </li>
        ))}
      </ul>
      
      <div className="sidebar-footer">
        <div className="user-info">
          <div className="user-avatar">
            <i className="fas fa-user"></i>
          </div>
          <div className="user-details">
            <div className="user-name">Admin User</div>
            <div className="user-role">Inventory Manager</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
