// src/services/api.js
import axios from "axios";

const API_BASE_URL = "http://localhost:5000/api";

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    "Content-Type": "application/json",
  },
});

// ------------------ ORDERS ------------------

export const getOrders = async () => {
  try {
    const response = await api.get("/orders");
    return response.data;
  } catch (err) {
    console.error("Error fetching orders:", err);
    throw err;
  }
};

export const addOrder = async (orderData) => {
  try {
    const payload = {
      orderId: orderData.orderId,
      customerName: orderData.customerName,
      product: orderData.product,
      quantity: Number(orderData.quantity),
      orderDate: orderData.orderDate,
      status: orderData.status,
    };
    const response = await api.post("/orders", payload);
    return response.data;
  } catch (err) {
    console.error("Error adding order:", err);
    throw err;
  }
};

export const updateOrder = async (id, updatedData) => {
  try {
    const response = await api.put(`/orders/${id}`, updatedData);
    return response.data;
  } catch (err) {
    console.error(`Error updating order ${id}:`, err);
    throw err;
  }
};

export const deleteOrder = async (id) => {
  try {
    const response = await api.delete(`/orders/${id}`);
    return response.data;
  } catch (err) {
    console.error(`Error deleting order ${id}:`, err);
    throw err;
  }
};

// ------------------ AUTH ------------------

// Register a new user
export const registerUser = async (userData) => {
  try {
    const response = await api.post("/register", userData); // POST /api/register
    return response.data;
  } catch (err) {
    console.error("Error registering user:", err);
    throw err;
  }
};

// Login a user
export const loginUser = async (userData) => {
  try {
    const response = await api.post("/login", userData); // POST /api/login
    return response.data;
  } catch (err) {
    console.error("Error logging in:", err);
    throw err;
  }
};

export default api;
