import React, { useState } from 'react';
import { addOrder } from '../services/api'; // Import API service for adding orders

function OrderForm({ onAddOrder }) {
  const [formData, setFormData] = useState({
    orderId: '',
    customerName: '',
    product: '',
    quantity: '',
    orderDate: '',
    status: 'Pending', // Default status
  });
  const [errors, setErrors] = useState({}); // State for validation errors
  const [showModal, setShowModal] = useState(false); // State to control modal visibility

  // Handle input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Validate form inputs
  const validate = () => {
    let errors = {};
    if (!formData.orderId) errors.orderId = 'Order ID is required';
    if (!formData.customerName) errors.customerName = 'Customer Name is required';
    if (!formData.product) errors.product = 'Product is required';
    if (!formData.quantity || isNaN(formData.quantity) || formData.quantity <= 0) 
      errors.quantity = 'Quantity must be a positive number';
    if (!formData.orderDate) errors.orderDate = 'Order Date is required';
    if (!formData.status) errors.status = 'Order Status is required';
    return errors;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validate();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors); // Set errors
      setShowModal(true); // Show the modal
      return; // Stop submission
    }
    
    try {
      await addOrder(formData); // Send data to API
      onAddOrder(); // Callback to refresh the order list
      setFormData({ // Reset form
        orderId: '',
        customerName: '',
        product: '',
        quantity: '',
        orderDate: '',
        status: 'Pending',
      });
      setErrors({}); // Clear errors
      setShowModal(false); // Ensure modal is hidden
    } catch (error) {
      console.error('Error adding order:', error);
      // Add error handling if needed
    }
  };

  // Function to close the modal
  const closeModal = () => {
    setShowModal(false);
    setErrors({}); // Clear errors when closing
  };

  return (
    <>
      <form onSubmit={handleSubmit} className="order-form">
        <h2>Add New Order</h2>
        <div>
          <label>Order ID:</label>
          <input 
            type="text" 
            name="orderId" 
            value={formData.orderId} 
            onChange={handleChange} 
            placeholder="Enter Order ID" 
          />
        </div>
        <div>
          <label>Customer Name:</label>
          <input 
            type="text" 
            name="customerName" 
            value={formData.customerName} 
            onChange={handleChange} 
            placeholder="Enter Customer Name" 
          />
        </div>
        <div>
          <label>Product Ordered:</label>
          <input 
            type="text" 
            name="product" 
            value={formData.product} 
            onChange={handleChange} 
            placeholder="e.g., Cake" 
          />
        </div>
        <div>
          <label>Quantity:</label>
          <input 
            type="number" 
            name="quantity" 
            value={formData.quantity} 
            onChange={handleChange} 
            placeholder="Enter Quantity" 
          />
        </div>
        <div>
          <label>Order Date:</label>
          <input 
            type="date" 
            name="orderDate" 
            value={formData.orderDate} 
            onChange={handleChange} 
          />
        </div>
        <div>
          <label>Order Status:</label>
          <select 
            name="status" 
            value={formData.status} 
            onChange={handleChange}
          >
            <option value="Pending">Pending</option>
            <option value="Completed">Completed</option>
          </select>
        </div>
        <button type="submit">Add Order</button>
      </form>

      {showModal && (
        <div className="error-modal-overlay">
          <div className="error-modal">
            <h3>Validation Errors</h3>
            <ul>
              {Object.values(errors).map((error, index) => (
                <li key={index}>{error}</li>
              ))}
            </ul>
            <button onClick={closeModal}>Close</button>
          </div>
        </div>
      )}
    </>
  );
}

export default OrderForm;
