import React, { useState } from "react";
import "./AuthPage.css";

function AuthPage({ onLogin }) {
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);

  const [loginData, setLoginData] = useState({ username: "", password: "" });
  const [registerData, setRegisterData] = useState({ username: "", email: "", password: "" });

  // Handle input changes
  const handleLoginChange = (e) => setLoginData({ ...loginData, [e.target.name]: e.target.value });
  const handleRegisterChange = (e) => setRegisterData({ ...registerData, [e.target.name]: e.target.value });

  // Submit handlers
  const handleLoginSubmit = (e) => {
    e.preventDefault();
    console.log("Logging in:", loginData);
    onLogin(); // Redirect to dashboard
  };

  const handleRegisterSubmit = (e) => {
    e.preventDefault();
    console.log("Registering:", registerData);
    onLogin(); // Redirect to dashboard
  };

  return (
    <div className="h-screen flex flex-col items-center justify-center bg-gray-100 p-4">
      <h1 className="text-3xl font-bold mb-8 text-gray-700">Sweet Crust Bakery</h1>

      {/* Buttons */}
      <div className="flex gap-4 mb-6">
        <button
          className="px-6 py-2 bg-indigo-600 hover:bg-indigo-700 text-white rounded-lg transition duration-300"
          onClick={() => { setShowLogin(!showLogin); setShowRegister(false); }}
        >
          Login
        </button>
        <button
          className="px-6 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg transition duration-300"
          onClick={() => { setShowRegister(!showRegister); setShowLogin(false); }}
        >
          Register
        </button>
      </div>

      {/* Login Form */}
      {showLogin && (
        <form
          onSubmit={handleLoginSubmit}
          className="form-card fade-in mb-4"
        >
          <input
            type="text"
            name="username"
            placeholder="Username"
            value={loginData.username}
            onChange={handleLoginChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={loginData.password}
            onChange={handleLoginChange}
            required
          />
          <button type="submit" className="submit-btn">Login</button>
        </form>
      )}

      {/* Register Form */}
      {showRegister && (
        <form
          onSubmit={handleRegisterSubmit}
          className="form-card fade-in"
        >
          <input
            type="text"
            name="username"
            placeholder="Username"
            value={registerData.username}
            onChange={handleRegisterChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={registerData.email}
            onChange={handleRegisterChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={registerData.password}
            onChange={handleRegisterChange}
            required
          />
          <button type="submit" className="submit-btn bg-green-600 hover:bg-green-700">
            Register
          </button>
        </form>
      )}
    </div>
  );
}

export default AuthPage;
