import React, { useState } from "react";
import "./AuthPage.css";

function AuthPage({ onLogin }) {
  const [isRegister, setIsRegister] = useState(false);
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    password: ""
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isRegister) {
      console.log("Registering user:", formData);
      // TODO: call backend register API
      onLogin(); // log in automatically after register (optional)
    } else {
      console.log("Logging in:", formData);
      // TODO: call backend login API
      onLogin(); // log in after successful login
    }
  };

  return (
    <div className="h-screen flex items-center justify-center bg-gray-100">
      <div className="w-full max-w-md bg-white shadow-xl rounded-2xl p-8 relative overflow-hidden">
        <h2 className="text-2xl font-bold text-center mb-6 text-gray-700">
          {isRegister ? "Create an Account" : "Welcome Back"}
        </h2>

        {/* Form */}
        <form
          onSubmit={handleSubmit}
          className={`form-container ${isRegister ? "fade-in" : "fade-out"}`}
        >
          <input
            type="text"
            name="username"
            placeholder="Username"
            value={formData.username}
            onChange={handleChange}
            className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-400 outline-none mb-3"
            required
          />

          {isRegister && (
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-400 outline-none mb-3"
              required
            />
          )}

          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-400 outline-none mb-3"
            required
          />

          <button
            type="submit"
            className="w-full bg-indigo-600 hover:bg-indigo-700 text-white font-semibold py-2 rounded-lg transition duration-300"
          >
            {isRegister ? "Register" : "Login"}
          </button>
        </form>

        {/* Toggle */}
        <p className="text-center text-gray-600 mt-4">
          {isRegister ? "Already have an account?" : "Don't have an account?"}{" "}
          <button
            onClick={() => setIsRegister(!isRegister)}
            className="text-indigo-600 hover:underline font-medium"
          >
            {isRegister ? "Login here" : "Register here"}
          </button>
        </p>
      </div>
    </div>
  );
}

export default AuthPage;
