import React, { useState } from "react";
import "./AuthPage.css";

function AuthPage({ onLogin }) {
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);

  const [loginData, setLoginData] = useState({ username: "", password: "" });
  const [registerData, setRegisterData] = useState({ username: "", email: "", password: "" });

  // Handle input changes
  const handleLoginChange = (e) => setLoginData({ ...loginData, [e.target.name]: e.target.value });
  const handleRegisterChange = (e) =>
    setRegisterData({ ...registerData, [e.target.name]: e.target.value });

  // Submit handlers
  const handleLoginSubmit = (e) => {
    e.preventDefault();
    console.log("Logging in:", loginData);
    onLogin(); // Redirect to dashboard
  };

  const handleRegisterSubmit = (e) => {
    e.preventDefault();
    console.log("Registering:", registerData);
    onLogin(); // Redirect to dashboard
  };

  return (
    <div className="auth-wrapper">
      <div className="auth-form">
        <h1 className="brand-title">Sweet Crust Bakery</h1>

        {/* Toggle Buttons */}
        <div style={{ display: "flex", gap: "15px", marginBottom: "20px" }}>
          <button
            className="btn-primary"
            style={{ backgroundColor: "#3498db" }}
            onClick={() => {
              setShowLogin(!showLogin);
              setShowRegister(false);
            }}
          >
            Login
          </button>
          <button
            className="btn-primary"
            style={{ backgroundColor: "#2980b9" }}
            onClick={() => {
              setShowRegister(!showRegister);
              setShowLogin(false);
            }}
          >
            Register
          </button>
        </div>

        {/* Login Form */}
        {showLogin && (
          <form onSubmit={handleLoginSubmit} className="form-container fade-in">
            <input
              type="text"
              name="username"
              placeholder="Username"
              value={loginData.username}
              onChange={handleLoginChange}
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={loginData.password}
              onChange={handleLoginChange}
              required
            />
            <button type="submit" className="btn-primary">
              Login
            </button>
          </form>
        )}

        {/* Register Form */}
        {showRegister && (
          <form onSubmit={handleRegisterSubmit} className="form-container fade-in">
            <input
              type="text"
              name="username"
              placeholder="Username"
              value={registerData.username}
              onChange={handleRegisterChange}
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={registerData.email}
              onChange={handleRegisterChange}
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={registerData.password}
              onChange={handleRegisterChange}
              required
            />
            <button type="submit" className="btn-primary">
              Register
            </button>
          </form>
        )}
      </div>
    </div>
  );
}

export default AuthPage;
