import React, { useState } from "react";

function BookForm({ onAdd }) {
  const [form, setForm] = useState({
    title: "",
    author: "",
    category: "",
    price: "",
    quantity: "",
  });

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  function handleSubmit(e) {
    e.preventDefault();
    if (!form.title || !form.author) return alert("Fill in all fields!");
    onAdd({
      ...form,
      price: parseFloat(form.price),
      quantity: parseInt(form.quantity, 10),
    });
    setForm({ title: "", author: "", category: "", price: "", quantity: "" });
  }

  return (
    <form onSubmit={handleSubmit} className="p-4 bg-white rounded shadow space-y-3">
      <h2 className="text-xl font-semibold">Add Book</h2>
      <input name="title" placeholder="Title" value={form.title} onChange={handleChange} className="border p-2 w-full rounded" />
      <input name="author" placeholder="Author" value={form.author} onChange={handleChange} className="border p-2 w-full rounded" />
      <input name="category" placeholder="Category" value={form.category} onChange={handleChange} className="border p-2 w-full rounded" />
      <input name="price" type="number" placeholder="Price" value={form.price} onChange={handleChange} className="border p-2 w-full rounded" />
      <input name="quantity" type="number" placeholder="Quantity" value={form.quantity} onChange={handleChange} className="border p-2 w-full rounded" />
      <button className="bg-blue-500 text-white px-4 py-2 rounded">Add</button>
    </form>
  );
}

export default BookForm;
