// src/components/OrderForm.js
import React, { useState } from "react";
import { addOrder } from "../services/api";

function OrderForm({ onAddOrder }) {
  const [formData, setFormData] = useState({
    orderId: "",
    customerName: "",
    product: "",
    quantity: "",
    orderDate: "",
    status: "Pending"
  });
  const [errors, setErrors] = useState({});
  const [showModal, setShowModal] = useState(false);

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const validate = () => {
    const errs = {};
    if (!formData.orderId) errs.orderId = "Order ID is required";
    if (!formData.customerName) errs.customerName = "Customer Name is required";
    if (!formData.product) errs.product = "Product is required";
    if (!formData.quantity || formData.quantity <= 0) errs.quantity = "Quantity must be positive";
    if (!formData.orderDate) errs.orderDate = "Order Date is required";
    return errs;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validate();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      setShowModal(true);
      return;
    }

    try {
      await addOrder(formData);
      onAddOrder();
      setFormData({ orderId: "", customerName: "", product: "", quantity: "", orderDate: "", status: "Pending" });
      setErrors({});
      setShowModal(false);
    } catch (err) {
      console.error("Failed to add order:", err);
    }
  };

  const closeModal = () => { setShowModal(false); setErrors({}); };

  return (
    <>
      <form onSubmit={handleSubmit} className="order-form">
        <h2>Add New Order</h2>
        <input type="text" name="orderId" placeholder="Order ID" value={formData.orderId} onChange={handleChange} />
        <input type="text" name="customerName" placeholder="Customer Name" value={formData.customerName} onChange={handleChange} />
        <input type="text" name="product" placeholder="Product" value={formData.product} onChange={handleChange} />
        <input type="number" name="quantity" placeholder="Quantity" value={formData.quantity} onChange={handleChange} />
        <input type="date" name="orderDate" value={formData.orderDate} onChange={handleChange} />
        <select name="status" value={formData.status} onChange={handleChange}>
          <option value="Pending">Pending</option>
          <option value="Completed">Completed</option>
        </select>
        <button type="submit">Add Order</button>
      </form>

      {showModal && (
        <div className="error-modal-overlay">
          <div className="error-modal">
            <h3>Validation Errors</h3>
            <ul>{Object.values(errors).map((err, i) => <li key={i}>{err}</li>)}</ul>
            <button onClick={closeModal}>Close</button>
          </div>
        </div>
      )}
    </>
  );
}

export default OrderForm;
