import { useState, useEffect } from 'react';
import api from '../services/api';

const StudentForm = ({ student, onSave, onCancel }) => {
  const [form, setForm] = useState({ name: '', email: '', course: '' });

  useEffect(() => {
    if (student) setForm(student);
  }, [student]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (student) {
      await api.put(`/students/${student.id}`, form);
    } else {
      await api.post('/students', form);
    }
    onSave();
  };

  return (
    <form onSubmit={handleSubmit} className="mb-4">
      <input type="text" placeholder="Name" value={form.name} onChange={(e) => setForm({ ...form, name: e.target.value })} required className="border p-2 mr-2" />
      <input type="email" placeholder="Email" value={form.email} onChange={(e) => setForm({ ...form, email: e.target.value })} required className="border p-2 mr-2" />
      <input type="text" placeholder="Course" value={form.course} onChange={(e) => setForm({ ...form, course: e.target.value })} required className="border p-2 mr-2" />
      <button type="submit" className="bg-blue-500 text-white p-2">Save</button>
      <button type="button" onClick={onCancel} className="bg-gray-500 text-white p-2 ml-2">Cancel</button>
    </form>
  );
};

export default StudentForm;
