import React from 'react';
import { Doughnut, Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

const StockChart = ({ products }) => {
  // Categorize products by stock level
  const stockLevels = {
    low: products.filter(p => p.quantity < 5).length,
    medium: products.filter(p => p.quantity >= 5 && p.quantity < 20).length,
    high: products.filter(p => p.quantity >= 20).length
  };

  // Group products by category
  const categories = [...new Set(products.map(p => p.category))];
  const categoryCounts = categories.map(category => 
    products.filter(p => p.category === category).length
  );

  const stockData = {
    labels: ['Low Stock', 'Medium Stock', 'High Stock'],
    datasets: [
      {
        data: [stockLevels.low, stockLevels.medium, stockLevels.high],
        backgroundColor: [
          '#ef4444',
          '#f59e0b',
          '#10b981'
        ],
        borderWidth: 0,
      },
    ],
  };

  const categoryData = {
    labels: categories,
    datasets: [
      {
        label: 'Number of Books',
        data: categoryCounts,
        backgroundColor: 'rgba(99, 102, 241, 0.7)',
        borderColor: 'rgba(99, 102, 241, 1)',
        borderWidth: 1,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'bottom',
      },
    },
  };

  return (
    <div className="charts-container">
      <div className="chart">
        <h3>Stock Levels</h3>
        <Doughnut data={stockData} options={options} />
      </div>
      
      <div className="chart">
        <h3>Books by Category</h3>
        <Bar data={categoryData} options={options} />
      </div>
    </div>
  );
};

export default StockChart;
