import React, { useState, useEffect } from 'react';
import Dashboard from './components/dashboard';
import ProductForm from './components/ProductForm';
import ProductTable from './components/ProductTable';
import Sidebar from './components/Sidebar';
import './App.css';

function App() {
  const [products, setProducts] = useState([]);
  const [activeView, setActiveView] = useState('dashboard');

  // Load products from localStorage on initial render
  useEffect(() => {
    const savedProducts = JSON.parse(localStorage.getItem('bookstore-products'));
    if (savedProducts) {
      setProducts(savedProducts);
    }
  }, []);

  // Save products to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('bookstore-products', JSON.stringify(products));
  }, [products]);

  const addProduct = (product) => {
    const newProduct = {
      ...product,
      id: Date.now(), // Simple unique ID
    };
    setProducts([...products, newProduct]);
  };

  const updateProduct = (id, updatedProduct) => {
    setProducts(products.map(product => 
      product.id === id ? { ...updatedProduct, id } : product
    ));
  };

  const deleteProduct = (id) => {
    setProducts(products.filter(product => product.id !== id));
  };

  const renderActiveView = () => {
    switch (activeView) {
      case 'dashboard':
        return <Dashboard products={products} />;
      case 'add':
        return <ProductForm onSubmit={addProduct} />;
      case 'manage':
        return (
          <ProductTable 
            products={products} 
            onEdit={updateProduct} 
            onDelete={deleteProduct} 
          />
        );
      default:
        return <Dashboard products={products} />;
    }
  };

  return (
    <div className="app">
      <Sidebar activeView={activeView} setActiveView={setActiveView} />
      <main className="main-content">
        {renderActiveView()}
      </main>
    </div>
  );
}

export default App;
