import React, { useState, useEffect } from "react";
import { loadBooks, saveBooks } from "./utils/storage";
import BookForm from "./components/bookform";
import BookTable from "./components/booktable";
import Dashboard from "./components/dashboard";

function App() {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    setBooks(loadBooks());
  }, []);

  useEffect(() => {
    saveBooks(books);
  }, [books]);

  function addBook(book) {
    setBooks([...books, { ...book, id: Date.now() }]);
  }

  function updateBook(updated) {
    setBooks(books.map(b => (b.id === updated.id ? updated : b)));
  }

  function deleteBook(id) {
    setBooks(books.filter(b => b.id !== id));
  }

  return (
    <div className="p-6 space-y-6">
      <h1 className="text-3xl font-bold">ðŸ“š Bookstore Inventory</h1>
      <dashboard books={books} />
      <bookform onAdd={addBook} onUpdate={updateBook} />
      <booktable books={books} onDelete={deleteBook} onUpdate={updateBook} />
    </div>
  );
}

export default App;
