import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api'; // Adjust this to your Node.js server URL

export const getOrders = () => axios.get(`${API_BASE_URL}/orders`);
export const addOrder = (orderData) => axios.post(`${API_BASE_URL}/orders`, orderData);
export const updateOrder = (id, updatedData) => axios.put(`${API_BASE_URL}/orders/${id}`, updatedData);
export const deleteOrder = (id) => axios.delete(`${API_BASE_URL}/orders/${id}`);
