// src/App.js
import React, { useState, useEffect } from "react";
import OrderForm from "./components/OrderForm";
import OrderTable from "./components/OrderTable";
import { getOrders } from "./services/api";
import AuthPage from "./components/AuthPage";
import "./App.css";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);

  // Called after login/register
  const handleLogin = () => setIsLoggedIn(true);

  // Fetch orders from backend
  const fetchOrders = async () => {
    setLoading(true);
    try {
      const data = await getOrders();
      setOrders(data || []);
    } catch (err) {
      console.error("Error fetching orders:", err);
      setOrders([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (isLoggedIn) fetchOrders();
  }, [isLoggedIn]);

  if (!isLoggedIn) return <AuthPage onLogin={handleLogin} />;

  return (
    <div className="app-container">
      <div className="app-card">
        <h1 className="brand-title">Sweet Crust Bakery Order Management</h1>

        {loading && <p>Loading orders...</p>}
        {!loading && orders.length === 0 && <p>No orders found yet!</p>}
        {!loading && orders.length > 0 && (
          <OrderTable orders={orders} onUpdate={fetchOrders} onDelete={fetchOrders} />
        )}
        <OrderForm onAddOrder={fetchOrders} />
      </div>
    </div>
  );
}

export default App;
