import React from 'react';
import { updateOrder, deleteOrder } from '../services/api';

function OrderTable({ orders, onUpdate, onDelete }) {
  // Update order (e.g., status)
  const handleUpdate = async (id, updatedData) => {
    try {
      await updateOrder(id, updatedData);
      onUpdate(); // Refresh table
    } catch (error) {
      console.error('Error updating order:', error);
    }
  };

  // Delete order
  const handleDelete = async (id) => {
    try {
      await deleteOrder(id);
      onDelete(); // Refresh table
    } catch (error) {
      console.error('Error deleting order:', error);
    }
  };

  return (
    <div className="order-table">
      <h2>Existing Orders</h2>
      {orders.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Order ID</th>
              <th>Customer Name</th>
              <th>Product</th>
              <th>Quantity</th>
              <th>Order Date</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <tr key={order.id}>
                <td>{order.id}</td>
                <td>{order.customer}</td>
                <td>{order.item}</td>
                <td>{order.quantity}</td>
                <td>{new Date(order.created_at).toLocaleDateString()}</td>
                <td>
                  <select 
                    value={order.status} 
                    onChange={(e) => handleUpdate(order.id, { status: e.target.value })}
                  >
                    <option value="Pending">Pending</option>
                    <option value="Completed">Completed</option>
                  </select>
                </td>
                <td>
                  <button onClick={() => handleDelete(order.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No orders found.</p>
      )}
    </div>
  );
}

export default OrderTable;
