// components/Inventory.js
import React from "react";
import InventoryTable from "./InventoryTable";
import ProductForm from "./ProductForm";

const Inventory = ({ 
  books, 
  onAdd, 
  onEdit, 
  onDelete, 
  isFormOpen, 
  editingBook, 
  onAddBook, 
  onUpdateBook, 
  onCloseForm 
}) => {
  return (
    <div className="page">
      <div className="page-header">
        <h1>Inventory Management</h1>
        <button className="add-btn" onClick={onAdd}>
          Add New Book
        </button>
      </div>
      
      <InventoryTable books={books} onEdit={onEdit} onDelete={onDelete} />
      
      {isFormOpen && (
        <ProductForm 
          book={editingBook}
          onSubmit={editingBook ? onUpdateBook : onAddBook}
          onCancel={onCloseForm}
        />
      )}
    </div>
  );
};

export default Inventory;
