import React from "react";
import "./Sidebar.css"; // we’ll style separately later if needed
import ProductForm from "./ProductForm";

export default function Sidebar({ currentPage, onChangePage,onAdd }) {
  return (
    <aside className="sidebar">
      <div className="logo">
        <div className="badge">B</div>
        <div>
          <div style={{ fontWeight: 700 }}>BookStore</div>
          <div style={{ fontSize: 12, color: "#6b7280" }}>
            Inventory Management
          </div>
        </div>
      </div>

      <nav className="nav">
        <button
          className={currentPage === "overview" ? "active" : ""}
          onClick={() => onChangePage("overview")}
        >
          Overview
        </button>
        <button
          className={currentPage === "inventory" ? "active" : ""}
          onClick={() => onChangePage("inventory")}
        >
          Inventory
        </button>
        <button
          className={currentPage === "analytics" ? "active" : ""}
          onClick={() => onChangePage("analytics")}
        >
          Analytics
        </button>
        <button
          className={currentPage === "search" ? "active" : ""}
          onClick={() => onChangePage("search")}
        >
          Search
        </button>
      </nav>

      <div style={{ marginTop: "auto" }}>
        <button className="add-btn" onClick={onAdd}>
          Add New Book
        </button>
      </div>
    </aside>
  );
}
