import React, { useState, useEffect } from "react";
import OrderForm from "./components/OrderForm";
import OrderTable from "./components/OrderTable";
import { getOrders } from "./services/api"; // API service
import "./App.css";
import AuthPage from "./components/AuthPage";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // ✅ Called when user logs in
  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  // Fetch orders when component mounts or after login
  useEffect(() => {
    if (isLoggedIn) {
      fetchOrders();
    }
  }, [isLoggedIn]);

  // Fetch orders from backend
  const fetchOrders = async () => {
    try {
      setLoading(true);
      const data = await getOrders(); // api.js already returns data
      setOrders(data);
      setError(null);
    } catch (err) {
      console.error("Error fetching orders:", err);
      setError("Failed to load orders. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  // Callbacks for CRUD operations
  const handleAddOrder = () => fetchOrders();
  const handleUpdateOrder = () => fetchOrders();
  const handleDeleteOrder = () => fetchOrders();

  // ✅ Show AuthPage first, then dashboard
  if (!isLoggedIn) {
    return <AuthPage onLogin={handleLogin} />;
  }

  return (
    <div className="app-container">
      <h1>Sweet Crust Bakery Order Management</h1>

      {loading && <p>Loading orders...</p>}
      {error && <p className="error-message">{error}</p>}

      {!loading && !error && (
        <>
          <OrderForm onAddOrder={handleAddOrder} />
          <OrderTable
            orders={orders}
            onUpdate={handleUpdateOrder}
            onDelete={handleDeleteOrder}
          />
        </>
      )}
    </div>
  );
}

export default App;
