import axios from "axios";

const API_BASE_URL = "http://localhost:5000";

// Axios instance
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: { "Content-Type": "application/json" },
});

// --- Orders ---
export const getOrders = async () => (await api.get("/api/orders")).data;
export const addOrder = async (orderData) => (await api.post("/api/orders", orderData)).data;
export const updateOrder = async (id, updatedData) => (await api.put(`/api/orders/${id}`, updatedData)).data;
export const deleteOrder = async (id) => (await api.delete(`/api/orders/${id}`)).data;

// --- Auth ---
export const registerUser = async (userData) => (await api.post("/auth/register", userData)).data;
export const loginUser = async (loginData) => (await api.post("/auth/login", loginData)).data;

export default api;
