// components/StockChart.js
import React from "react";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Register ChartJS components
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const StockChart = ({ books }) => {
  // Prepare data for chart
  const categories = [...new Set(books.map((book) => book.category))];

  const stockByCategory = categories.map((category) => {
    const booksInCategory = books.filter(
      (book) => book.category === category
    );
    return booksInCategory.reduce((sum, book) => sum + book.quantity, 0);
  });

  // Conditional colors (red if out of stock, blue otherwise)
  const barColors = stockByCategory.map((qty) =>
    qty <= 5 ? "rgba(195, 0, 0, 0.95)" : "rgba(18, 6, 253, 0.7)"
  );

  const borderColors = stockByCategory.map((qty) =>
    qty <= 5 ? "rgba(195, 0, 0, 0.95)" : "rgba(3, 3, 3, 1)"
  );

  // Chart data configuration
  const data = {
    labels: categories,
    datasets: [
      {
        label: "stock",
        data: stockByCategory,
        backgroundColor: barColors,
        borderColor: borderColors,
        borderWidth: 2,
        borderRadius: 6, // Rounded bars
        barThickness: 40, // Consistent bar width
      },
    ],
  };

  // Chart options
  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: "top",
        labels: {
          font: {
            size: 14,
            weight: "bold",
          },
        },
      },
      title: {
        display: true,
        text: "ðŸ“Š Books Inventory by Category",
        font: {
          size: 18,
          weight: "bold",
        },
        color: "#222",
      },
      tooltip: {
        backgroundColor: "#111",
        titleColor: "#fff",
        bodyColor: "#fff",
        padding: 12,
        cornerRadius: 8,
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          font: {
            size: 12,
          },
        },
        title: {
          display: true,
          text: "Quantity",
          font: {
            size: 14,
            weight: "bold",
          },
        },
      },
      x: {
        ticks: {
          font: {
            size: 12,
          },
        },
        title: {
          display: true,
          text: "Category",
          font: {
            size: 14,
            weight: "bold",
          },
        },
      },
    },
  };

  return (
    <div className="p-4 bg-white shadow-md rounded-2xl h-[400px]">
      <Bar data={data} options={options} />
    </div>
  );
};

export default StockChart;
