import React from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  ResponsiveContainer,
} from "recharts";

function Dashboard({ books, onBorrow }) {
  const totalQty = books.reduce((sum, b) => sum + Number(b.quantity || 0), 0);
  const totalValue = books.reduce(
    (sum, b) => sum + Number(b.price || 0) * Number(b.quantity || 0),
    0
  );

  // Chart data
  const data = books.map(b => ({
    name: b.title,
    stock: Number(b.quantity || 0),
  }));

  const availableBooks = books.filter(b => b.quantity > 0);

  return (
    <div className="bg-white p-4 rounded shadow space-y-4">
      <h2 className="text-xl font-semibold">Dashboard</h2>

      {/* Stats */}
      <div>
        <p>Total Books in Stock: {totalQty}</p>
        <p>Total Inventory Value: M {totalValue.toFixed(2)}</p>
      </div>

      {/* Chart */}
      <div style={{ width: "100%", height: 300 }}>
        <ResponsiveContainer width="100%" height="100%">
          <BarChart data={data} margin={{ top: 20, right: 20, left: 0, bottom: 5 }}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis allowDecimals={false} />
            <Tooltip />
            <Bar dataKey="stock" fill="#3b82f6" />
          </BarChart>
        </ResponsiveContainer>
      </div>

      {/* Available Books + Borrow */}
      <div>
        <h3 className="text-lg font-semibold mb-2">Available Books</h3>
        {availableBooks.length === 0 ? (
          <p className="text-gray-500">No books available right now.</p>
        ) : (
          <ul className="space-y-2">
            {availableBooks.map(b => (
              <li
                key={b.id}
                className="flex justify-between items-center border p-2 rounded"
              >
                <span>
                  {b.title} â€” {b.author} ({b.quantity} left)
                </span>
                <button
                  onClick={() => onBorrow(b.id)}
                  className="bg-green-500 text-white px-3 py-1 rounded hover:bg-green-600"
                >
                  Borrow
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
