import React, { useState, useEffect } from 'react';
import OrderForm from './components/OrderForm';
import OrderTable from './components/OrderTable';
import { getOrders } from './services/api'; // Import API service
import './App.css'; // Optional: For basic styling

function App() {
  const [orders, setOrders] = useState([]); // State to hold the list of orders

  // Fetch orders when the component mounts
  useEffect(() => {
    fetchOrders();
  }, []);

  // Function to fetch orders from the API
  const fetchOrders = async () => {
    try {
      const response = await getOrders();
      setOrders(response.data); // Assuming the API returns an array of orders
    } catch (error) {
      console.error('Error fetching orders:', error);
      // You can add user-friendly error handling, e.g., alert('Failed to load orders');
    }
  };

  // Callback functions to refresh orders after adding, updating, or deleting
  const handleAddOrder = () => fetchOrders(); // Called from OrderForm
  const handleUpdateOrder = () => fetchOrders(); // Called from OrderTable
  const handleDeleteOrder = () => fetchOrders(); // Called from OrderTable

  return (
    <div className="app-container">
      <h1>Sweet Crust Bakery Order Management</h1>
      <OrderForm onAddOrder={handleAddOrder} /> {/* Pass callback to refresh after adding */}
      <OrderTable 
        orders={orders} 
        onUpdate={handleUpdateOrder} 
        onDelete={handleDeleteOrder} 
      /> {/* Pass orders and callbacks */}
    </div>
  );
}

export default App;
