import React, { useState, useEffect } from "react";
import OrderForm from "./components/OrderForm";
import OrderTable from "./components/OrderTable";
import { getOrders } from "./services/api";
import "./App.css";
import AuthPage from "./components/AuthPage";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Called after login/register
  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  // Fetch orders from backend
  const fetchOrders = async () => {
    setLoading(true);
    setError("");
    try {
      const data = await getOrders(); // api.js returns data array
      setOrders(data || []);
    } catch (err) {
      console.error("Error fetching orders:", err);
      setError("Failed to load orders. Please try again later.");
      setOrders([]);
    } finally {
      setLoading(false);
    }
  };

  // Fetch orders whenever dashboard loads
  useEffect(() => {
    if (isLoggedIn) fetchOrders();
  }, [isLoggedIn]);

  // Callbacks for CRUD operations
  const handleAddOrder = () => fetchOrders();
  const handleUpdateOrder = () => fetchOrders();
  const handleDeleteOrder = () => fetchOrders();

  // Show login/register first
  if (!isLoggedIn) {
    return <AuthPage onLogin={handleLogin} />;
  }

  // Dashboard
  return (
    <div className="app-container">
      <div className="app-card">
        <h1 className="brand-title">Sweet Crust Bakery Order Management</h1>

        {loading && <p>Loading orders...</p>}

        {!loading && orders.length === 0 && (
          <p className="error-message">
            No orders found yet. Start by adding one below!
          </p>
        )}

        {!loading && orders.length > 0 && (
          <OrderTable
            orders={orders}
            onUpdate={handleUpdateOrder}
            onDelete={handleDeleteOrder}
          />
        )}

        <OrderForm onAddOrder={handleAddOrder} />
      </div>
    </div>
  );
}

export default App;
