const StudentList = ({ students, onEdit, onDelete }) => {
  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
      {students.map((student) => (
        <div
          key={student.id}
          className="bg-gradient-to-r from-blue-800/70 to-indigo-800/70 p-4 rounded-lg shadow-lg border border-blue-600/50 hover:shadow-xl hover:scale-105 transition-all duration-300 animate-fade-in"
        >
          <h3 className="text-lg font-bold text-blue-200 mb-2">üë®‚Äçüéì {student.name}</h3>
          <p className="text-blue-300 mb-1">üìß {student.email}</p>
          <p className="text-blue-300 mb-3">üìö {student.course}</p>
          <div className="flex space-x-2">
            <button
              onClick={() => onEdit(student)}
              className="bg-yellow-500 hover:bg-yellow-600 text-white px-4 py-2 rounded-lg font-semibold transition-all duration-300 flex items-center"
            >
              ‚úèÔ∏è Edit
            </button>
            <button
              onClick={() => onDelete(student.id)}
              className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-lg font-semibold transition-all duration-300 flex items-center"
            >
              üóëÔ∏è Delete
            </button>
          </div>
        </div>
      ))}
      {students.length === 0 && <p className="text-blue-300 col-span-full text-center">No students yet. Add one! üéâ</p>}
    </div>
  );
};

export default StudentList;
