// src/components/AuthPage.js
import React, { useState } from "react";
import { registerUser, loginUser } from "../services/api";
import "./AuthPage.css";

function AuthPage({ onLogin }) {
  const [showLogin, setShowLogin] = useState(true); // default to login
  const [loginData, setLoginData] = useState({ username: "", password: "" });
  const [registerData, setRegisterData] = useState({ username: "", email: "", password: "" });
  const [errorMsg, setErrorMsg] = useState("");
  const [successMsg, setSuccessMsg] = useState("");

  // Input handlers
  const handleLoginChange = (e) => setLoginData({ ...loginData, [e.target.name]: e.target.value });
  const handleRegisterChange = (e) => setRegisterData({ ...registerData, [e.target.name]: e.target.value });

  // Submit handlers
  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    setErrorMsg("");
    setSuccessMsg("");
    try {
      const res = await loginUser(loginData);
      if (res.success) {
        setSuccessMsg("Login successful!");
        onLogin(); // redirect to dashboard
      } else {
        setErrorMsg(res.message || "Invalid credentials");
      }
    } catch (err) {
      setErrorMsg("Login failed. Try again.");
      console.error(err);
    }
  };

  const handleRegisterSubmit = async (e) => {
    e.preventDefault();
    setErrorMsg("");
    setSuccessMsg("");
    try {
      const res = await registerUser(registerData);
      if (res.success) {
        setSuccessMsg("Registration successful! You can now login.");
        setShowLogin(true); // switch to login form
      } else {
        setErrorMsg(res.message || "Registration failed");
      }
    } catch (err) {
      setErrorMsg("Registration failed. Try again.");
      console.error(err);
    }
  };

  return (
    <div className="h-screen flex flex-col items-center justify-center bg-bakery-dark p-4">
      <h1 className="text-4xl font-bold mb-6 text-white">Sweet Crust Bakery</h1>

      <div className="flex gap-4 mb-6">
        <button
          className={`px-8 py-3 rounded-lg text-white font-bold transition ${
            showLogin ? "bg-blue-700" : "bg-blue-500 hover:bg-blue-600"
          }`}
          onClick={() => setShowLogin(true)}
        >
          Login
        </button>
        <button
          className={`px-8 py-3 rounded-lg text-white font-bold transition ${
            !showLogin ? "bg-green-700" : "bg-green-500 hover:bg-green-600"
          }`}
          onClick={() => setShowLogin(false)}
        >
          Register
        </button>
      </div>

      {errorMsg && <p className="error-msg">{errorMsg}</p>}
      {successMsg && <p className="success-msg">{successMsg}</p>}

      {showLogin ? (
        <form onSubmit={handleLoginSubmit} className="auth-form">
          <input type="text" name="username" placeholder="Username" value={loginData.username} onChange={handleLoginChange} required />
          <input type="password" name="password" placeholder="Password" value={loginData.password} onChange={handleLoginChange} required />
          <button type="submit" className="btn-primary">Login</button>
        </form>
      ) : (
        <form onSubmit={handleRegisterSubmit} className="auth-form">
          <input type="text" name="username" placeholder="Username" value={registerData.username} onChange={handleRegisterChange} required />
          <input type="email" name="email" placeholder="Email" value={registerData.email} onChange={handleRegisterChange} required />
          <input type="password" name="password" placeholder="Password" value={registerData.password} onChange={handleRegisterChange} required />
          <button type="submit" className="btn-primary bg-green-600 hover:bg-green-700">Register</button>
        </form>
      )}
    </div>
  );
}

export default AuthPage;
